import React, { Fragment } from 'react';


const BookInfo = (info) => {

  const booksList = books.length >0 ? books.map((book) => {
    return (
      <li className='list-group-item d-flex  justify-content-between align-items-center' key={book.id}>
        <div>{book.title}</div>
        <div className='btn-group' role='group'>
          <button type='button' className='btn btn-primary' onClick={()=> handleRead(book.id)}>
            Read
          </button>
          <button type='button' className='btn btn-danger'>
            Delete
          </button>
        </div>
      </li>
    )
  }) : 'there is no book available';

  return (
    <Fragment>
      <h2>Book Details</h2>
      {info.length > 0  ? info.map(book =>{
        return(
      <div>
      <p className='fw-bold'>{book.title}</p>
      <p className='fw-light'>{book.discription}</p>
      <p className='fst-italic'>{book.price}</p>
    </div> 
      )})
    :
      <div className='alert alert-secondary' role='alert'>
        There is no post selected yet. Please select!
      </div>

  }
    </Fragment>
  );
};

export default BookInfo;
